<script>
import modalWindowBackdrop from "../../components/page components/ModalWindowBackdrop.vue";
import Loading from 'vue-loading-overlay';
import 'vue-loading-overlay/dist/css/index.css';
import config from "../../assets/js/config.js";
import {useFetch} from "../../assets/js/getCategories.js";
import {reactive} from "vue";

export default {
  name: "ShopsCategories.vue",
  emits: ['someChanges'],
  props: ['isEditable'],

  data() {
    return {
      data: '',
      categories: '',
      showModal: false,
      editCategoryName: 0,
      count: 3,
      newCategoryName: '',
      changed: true,

      isLoading: false,
      fullPage: true,
      obj: {}
    }
  },
  components: { modalWindowBackdrop, Loading },
  created() {
    this.isLoading = true
  },
  mounted() {
    this.getCategories()



  },

  updated() {

    this.isLoading = false
  },
  setup() {
    const data = reactive(useFetch())

    console.log('data', data.data)
  },

  methods: {
    getCategories() {
      const myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");

      fetch(`${config.api.url}categories/`, {
        method: "GET",
        headers: myHeaders,
      })
          .then((response) => response.json())
          .then((result) => {

            result.success === true ? this.categories = result.categories : console.error("Can't load categories, fetch result: ", result)
          })
          .catch((error) => {console.error(error)});


    },

    updateCategoryList(target, id) {

      const myHeaders = new Headers();
      myHeaders.append("Authorization", `Bearer ${localStorage.getItem('token')}`);
      myHeaders.append("Content-Type", "application/json");

      fetch("http://62.113.96.171:3000/categories/" + id, {
        method: "PUT",
        headers: myHeaders,
        body: JSON.stringify({
          "name": target
        })
      })
          .then((response) => response.json())
          .then((result) => {
            this.getCategoriesList()
            this.$emit('someChanges')
          })
          .catch((error) => {console.error(error)});




    },

    deleteCategory(categoryId) {
      const myHeaders = new Headers();
      myHeaders.append("Content-Type", "application/json");
      myHeaders.append("Authorization", `Bearer ${localStorage.getItem('token')}`);

      fetch(`http://62.113.96.171:3000/categories/${categoryId}`, {
        method: "DELETE",
        headers: myHeaders,
      })
          .then((response) => response.json())
          .then(res => res)
          .catch((error) => {console.error(error)});



    },
  }
}
</script>

